CREATE VIEW orgi_vw_orgao_gt AS SELECT            o.orgi_dk CD_ORGAO,            o.orgi_nm_orgao NM_ORGAO,            fcr.cd_regiao,            fcr.nm_regiao,            fcr.cd_comarca,            fcr.nm_comarca,            fcr.cd_foro,            fcr.nm_foro,            t.tpor_dk CD_TPORGAO,            t.tpor_ds_tp_orgao NM_TPORGAO       FROM (SELECT f.cofo_dk CD_FORO,                    f.cofo_nm_foro NM_FORO,                    cr.cmrc_dk CD_COMARCA,                    cr.cmrc_nm_comarca NM_COMARCA,                    cr.regi_dk CD_REGIAO,                    cr.regi_nm_regiao NM_REGIAO               FROM (SELECT c.cmrc_dk,                            c.cmrc_nm_comarca,                            r.regi_dk,                            r.regi_nm_regiao                       FROM exadata.ORGI_COMARCA C,                            exadata.ORGI_REGIAO R,                            exadata.ORGI_REGIAO_COMARCA RC                      WHERE r.regi_dk = rc.recm_regi_dk                        AND rc.recm_cmrc_dk = c.cmrc_dk                        AND rc.recm_dt_fim IS NULL) CR,                    exadata.ORGI_FORO F              WHERE cr.cmrc_dk = f.cofo_cmrc_dk) FCR,            exadata.ORGI_ORGAO O,            exadata.ORGI_FORO_ORGAO FO,            exadata.ORGI_TP_ORGAO T      WHERE fo.forg_orgi_dk = o.orgi_dk        AND fcr.cd_foro = fo.forg_cofo_dk        AND t.tpor_dk = o.orgi_tpor_dk    ORDER BY CD_ORGAO
